import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { signin } from '../actions/userActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';

export default function EnquiryScreen(props) {
  const [user_id, setUser_id] = useState('');
  const [user_name, setUser_name] = useState('');
  const [account_group, setAccount_Group] = useState('');

  const redirect = props.location.search
    ? props.location.search.split('=')[1]
    : '/';

  const userSignin = useSelector((state) => state.userSignin);
  const { userInfo, loading, error } = userSignin;

  const dispatch = useDispatch();
  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(signin(user_id, user_name, account_group));
  };
  
  return (
    <div>
    <form className="form" onSubmit={submitHandler}>
      <div>
        <h1></h1>
      </div>
      {loading && <LoadingBox></LoadingBox>}
      {error && <MessageBox variant="danger">{error}</MessageBox>}
      <div>
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="user_name"
          placeholder="Enter name"
          required
          onChange={(e) => setUser_name(e.target.value)}
        ></input>
      </div>
      <div>
        <label htmlFor="mobileno">Mobile No</label>
        <input
          type="text"
          id="user_id"
          placeholder="Enter mobile no"
          required
          onChange={(e) => setUser_id(e.target.value)}
        ></input>
      </div>

      <div>
        <label htmlFor="Group">Select Group</label>
        <select id='account_group' onChange={(e) => setAccount_Group(e.target.value)}>
          <option>ABC</option>
          <option>XYZ</option>
        </select>
       
      </div>
     
      <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
    </form>
  </div>
  );
}
