import Axios from 'axios';
import {
  ORDER_CREATE_REQUEST, ORDER_CREATE_FAIL, ORDER_CREATE_SUCCESS,
  ORDER_LIST_REQUEST, ORDER_LIST_SUCCESS, ORDER_LIST_FAIL,
 } from "../constants/orderConstants";

 export const orderlist = ({}) => async (dispatch) => {
  dispatch({ type: ORDER_LIST_REQUEST });
  try {
    const { data } = await Axios.get(
      `/api/order/all`
    );
    dispatch({ type: ORDER_LIST_SUCCESS, payload: data });
  } catch (error) {
    dispatch({ type: ORDER_LIST_FAIL, payload: error.message });
  }
};


export const addOrder = (Name) => async (dispatch) => {
  dispatch({ type: ORDER_CREATE_REQUEST, payload: { Name } });
  try {
    const { data } = await Axios.post('/api/order/add', { Name });
    dispatch({ type: ORDER_CREATE_SUCCESS, payload: data });
    localStorage.setItem('orderAdd', JSON.stringify(data));
  } catch (error) {
    dispatch({
      type: ORDER_CREATE_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};
