import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { grouplist, group_details } from '../actions/accountActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import Product from '../components/acgroup';

export default function AccountScreen(props) {
  const [name, setName] = useState('');
  const [mobileno, setMobile_No] = useState('');
  const [Ac_Group, setAC_Group] = useState('');


  const redirect = props.location.search
    ? props.location.search.split('=')[1]
    : '/';

  const account_group = useSelector((state) => state.account_group);
  const { account_groupInfo, loading, error } = account_group;

  const acgrouplist = useSelector((state) => state.acgrouplist);
  const { listgroups, groupLoading, groupError } = acgrouplist;

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(grouplist({}));
  })
  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(group_details(name, mobileno, Ac_Group));
  };
  useEffect(() => {
    if (account_groupInfo) {
      props.history.push(redirect);
    }
  }, [props.history, redirect, account_groupInfo]);
  
  return (
    <div>
    <form className="form" onSubmit={submitHandler}>
      <div>
        <h1>Add Account</h1>
      </div>
      {loading && <LoadingBox></LoadingBox>}
      {error && <MessageBox variant="danger">{error}</MessageBox>}
      <div>
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="Name"
          placeholder="Enter name"
          required
          onChange={(e) => setName(e.target.value)}
        ></input>
      </div>
      
      <div>
        <label htmlFor="mobileno">Mobile No</label>
        <input type="text" id='Mobile_No' placeholder="Enter mobile no" required onChange={(e) => setMobile_No(e.target.value)} />      
      </div>

      {groupLoading && <LoadingBox></LoadingBox>}
      {groupError && <MessageBox variant="danger">{groupError}</MessageBox>}

      <div>
      <label>Select Account Group</label>
            <select name="Ac_Group" id="Ac_Group" onChange={(e) => setAC_Group(e.target.value)}>
                <option>---Select---</option>
                   {listgroups && listgroups.map((obj) => {
                        return <option> <Product key={obj._id} product={obj}></Product> </option>
                     })
                    }
            </select>
      </div>
     
      <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
    </form>
  </div>
  );
}
