import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link, useHistory } from 'react-router-dom';
import { addProduct } from '../actions/productActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import axios from 'axios';

export default function ProductCreateScreen(props) {
  const [name, setName] = useState('');
  const [seller, setSeller] = useState('');
  const [image, setImage] = useState('');
  const [price, setPrice] = useState('');
  const [category, setCategory] = useState('');
  const [brand, setBrand] = useState('');
  const [countInStock, setCountInStock] = useState('');
  const [rating, setRating] = useState('');
  const [numReview, setNumReview] = useState('');
  const [description, setDescription] = useState('');
  const [formData, setFormData] = useState({});

  const history = useHistory();

  const addProduct = useSelector((state) => state.addProduct);
  const { productInfo, loading, error } = addProduct;

  const dispatch = useDispatch();
  const submitHandler = (e) => {
    e.preventDefault();
      {
      dispatch(addProduct({name, seller, image, category, price, brand, countInStock, rating, numReview, description}));      
      }
  };
  useEffect(() => {
    if (productInfo) {
      history.push('./productlist');
    }
  }, [productInfo]);

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setFormData(values => ({ ...values, [name]: value }))
  };

  const handleFileChange = (e) => {
    const upload_file = e[0];
    setFormData(values => ({ ...values, image: upload_file }));
}

  const handleSubmit = async(event) => { 
    event.preventDefault();
    if(Validate()){
      await axios.post('/api/products/create',formData).then((res)=>{
        if(res.status === 200){
         
          alert('Record Added Successfully');
          setFormData({});
        }
      }).catch((error) => {
        throw error;
      });
    }else{
      alert('Please Fill required data')
    }
  };
  
  const Validate = () => {
    const fields = ['name','seller','image','price','brand','Category','rating', 'countInStock', 'numReview','description'];
    const data = fields.filter((obj)=>formData[obj]===undefined||'')
    if(data && data.length){
      return false
    }else{
      return true
    }
  };


  return (
    <div>
      <form className="form">
        <div>
          <h1>Create Product</h1>
        </div>
        {loading && <LoadingBox></LoadingBox>}
        {error && <MessageBox variant="danger">{error}</MessageBox>}
        <div>
          <label htmlFor="name">Name</label>
          <input
                  type="text"
                  name="name"
                  value={formData.name || ""}
                  onChange={handleChange} required/>
        </div>
        <div>
          <label htmlFor="seller">Seller</label>
          <input
                  type="text"
                  name="seller"
                  value={formData.seller || ""}
                  onChange={handleChange} required/>
        </div>
        <div>
          <label htmlFor="image">Image</label>
          <input
            type="file"
            id="image"
            placeholder="Choose file"
            required
            onChange={handleFileChange}
          ></input>
        </div>
        <div>
          <label htmlFor="confirmPassword">Price</label>
          <input
                  type="text"
                  name="price"
                  value={formData.price || ""}
                  onChange={handleChange} required/>
        </div>
        <div>
          <label htmlFor="name">Category</label>
          <input
                  type="text"
                  name="category"
                  value={formData.category || ""}
                  onChange={handleChange} required/>
        </div>
        <div>
          <label htmlFor="brand">Brand</label>
          <input
                  type="text"
                  name="brand"
                  value={formData.brand || ""}
                  onChange={handleChange} required/>
        </div>
        <div>
          <label htmlFor="countInstock">CountInStock</label>
          <input
                  type="text"
                  name="countInStock"
                  value={formData.countInStock || ""}
                  onChange={handleChange} required/>
        </div>
        <div>
          <label htmlFor="rating">Rating</label>
          <input
                  type="text"
                  name="rating"
                  value={formData.rating || ""}
                  onChange={handleChange} required/>
        </div>
        <div>
          <label htmlFor="numReview">NumReview</label>
          <input
                  type="text"
                  name="numReview"
                  value={formData.numReview || ""}
                  onChange={handleChange} required/>
        </div>
        <div>
          <label htmlFor="description">Description</label>
          <input
                  type="text"
                  name="description"
                  value={formData.description || ""}
                  onChange={handleChange} required/>
        </div>
        <div>
          <label />
          <button className="primary" type="submit" onClick={handleSubmit}>
            Create
          </button>
        </div>
      </form>
    </div>
  );
}
