import React, {useState, useEffect} from 'react';
import { Card } from 'react-bootstrap';
import "react-alice-carousel/lib/alice-carousel.css";
import AliceCarousel from 'react-alice-carousel';
import axios from "axios";

const SliderScreen = (props)=> {
    const [upcomingEvents, setUpcomingEvents] = useState([]);
      const responsive = {
          0: { items: 1 },
          568: { items: 3 },
          1024: { items: 3 },
      };
      useEffect(() => {
        axios.get('products/get')
        .then(items => {
            setUpcomingEvents(items.data);
        })
    
    }, [])
      return (
        <>
        <h3></h3>
        <AliceCarousel responsive={responsive} 
            disableButtonsControls={true} 
            disableDotsControls={true} 
            autoPlay={true} infinite={true} 
            autoPlayInterval={1800}>
                {
                    upcomingEvents && upcomingEvents.map((obj,index) => {
                        return(
                            <Card key={index} className='com-md-12 col-12'>
                                 <Card.Img variant="top" height='200' src={obj.slider} alt={obj.name}/>
                            </Card>
                        )
                    })
                }
          </AliceCarousel>
            </>
      ) 
  }

export default SliderScreen;
