import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { account_group } from '../actions/account_groupActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';

export default function AccountScreen(props) {
  const [Name, setName] = useState('');
  const [Mobile_No, setMobile_No] = useState('');
  const [Account_Group, setAccount_Group] = useState('');

  const redirect = props.location.search
    ? props.location.search.split('=')[1]
    : '/';

  const account_group = useSelector((state) => state.account_group);
  const { group, loading, error } = account_group;

  const dispatch = useDispatch();
  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(account_group(Name, Mobile_No, Account_Group));
  };
  useEffect(() => {
    if (group) {
      props.history.push(redirect);
    }
  }, [props.history, redirect, group]);
  
  return (
    <div>
    <form className="form" onSubmit={submitHandler}>
      <div>
        <h1>Add Account Group</h1>
      </div>
      {loading && <LoadingBox></LoadingBox>}
      {error && <MessageBox variant="danger">{error}</MessageBox>}
      <div>
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="user_name"
          placeholder="Enter name"
          required
          onChange={(e) => setName(e.target.value)}
        ></input>
      </div>
      <div>
        <label htmlFor="mobileno">Mobile No</label>
        <input
          type="text"
          id="user_id"
          placeholder="Enter mobile no"
          required
          onChange={(e) => setMobile_No(e.target.value)}
        ></input>
      </div>

      <div>
        <label htmlFor="Group">Select Group</label>
        <select id='account_group' onChange={(e) => setAccount_Group(e.target.value)}>
          <option>ABC</option>
          <option>XYZ</option>
        </select>
       
      </div>
     
      <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
    </form>
  </div>
  );
}
