import React, { useEffect, useState } from 'react';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import axios from 'axios';

export default function AccountScreen(props) {
  const [Name, setName] = useState('');
  const [Mobile_No, setMobile_No] = useState('');
  const [Ac_Group, setAc_Group] = useState('');
  const [formData, setFormData] = useState({});

  useEffect(async () => {
    axios.get( 'http://localhost:6000/api/accountGroup/all').then((res) => {
        setAc_Group(res.data);
    })
}, [])

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setFormData(values => ({ ...values, [name]: value }))
}
  const submitHandler = () => {
    
     
          const data = new FormData();
          data.append("Name", formData.Name)
          data.append("Mobile_No", formData.Mobile_No)
          data.append('Ac_Group', formData.Ac_Group)

          axios.post('http://localhost:6000/api/account/', data).then((res) => {
              if (res.status == 200) {
                  alert('Record Added Successfully');
                  setFormData({});
              }
          }).catch((error) => {
              throw error;
          });
     
  };
  
  return (
    <div>
    <form className="form" onSubmit={submitHandler}>
      <div>
        <h1>Add Account </h1>
      </div>
      <div>
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="Name"
          placeholder="Enter name"
          required
          onChange={handleChange}
        ></input>
      </div>

      <div>
        <label htmlFor="mobileno">Mobile No</label>
        <input
          type="text"
          id="Mobile_No"
          placeholder="Enter mobile no"
          required
          onChange={handleChange}
        ></input>
      </div>
      <div>
        <label htmlFor="ac_group">Account Group</label>
        <select name="Ac_Group" onChange={handleChange}>
                                <option>---Select---</option>
                                    {Ac_Group && Ac_Group.map((obj) => {
                                        return <option value={obj._id}>{obj.ac_group_title}</option>
                                    })
                                    }
        </select>
      </div>
      <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
    </form>
  </div>
  );
}
