import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { signin } from '../actions/userActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';

export default function EnquiryScreen(props) {
  const [Name, setName] = useState('');
  const [Mobile_No, setMobile_No] = useState('');
  const [Account_Group, setAccount_Group] = useState('');
  const [show, setShow] = useState(false);

  const redirect = props.location.search
    ? props.location.search.split('=')[1]
    : '/';

  const customerEnquiry = useSelector((state) => state.customerEnquiry);
  const { customerInfo, loading, error } = customerEnquiry;
  const handleShow = () => setShow(true);
  const dispatch = useDispatch();
  const submitHandler = (e) => {
    setShow(false);
    e.preventDefault();
    dispatch(signin(Name, Mobile_No, Account_Group));
  };
  useEffect(() => {
    if (customerInfo) {
      props.history.push(redirect);
    }
  }, [props.history, redirect, customerInfo]);
  
  return (
    <div>
    <form className="form" onSubmit={submitHandler}>
      <div>
        <h1></h1>
        <div>
        <label htmlFor="new">New</label>
        <input type="radio" value="New" name="New" onClick={handleShow}/>
      </div>
      <div>
        <label htmlFor="Existing">Existing</label>
        <input
          type="radio"
          name='Existing'
          value='Existing'
        ></input>
      </div>
      </div>
      {loading && <LoadingBox></LoadingBox>}
      {error && <MessageBox variant="danger">{error}</MessageBox>}
      <div>
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="user_name"
          placeholder="Enter name"
          required
          onChange={(e) => setName(e.target.value)}
        ></input>
      </div>
      <div>
        <label htmlFor="mobileno">Mobile No</label>
        <input
          type="text"
          id="user_id"
          placeholder="Enter mobile no"
          required
          onChange={(e) => setMobile_No(e.target.value)}
        ></input>
      </div>

      <div>
        <label htmlFor="Group">Select Group</label>
        <select id='account_group' onChange={(e) => setAccount_Group(e.target.value)}>
          <option>ABC</option>
          <option>XYZ</option>
        </select>
       
      </div>
     
      <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
    </form>
  </div>
  );
}
