import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { addService } from '../actions/serviceActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';

export default function SigninScreen(props) {
  const [title, setTitle] = useState('');
  const [sort_order, setSort_Order] = useState('');
  const [status, setStatus] = useState('');

  const redirect = props.location.search
    ? props.location.search.split('=')[1]
    : '/';

  const addS = useSelector((state) => state.addS);
  const { serviceAdd, loading, error } = addS;

  const dispatch = useDispatch();
  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(addService(title, sort_order, status));
  };
  useEffect(() => {
    if (serviceAdd) {
      props.history.push(redirect);
    }
  }, [props.history, redirect, serviceAdd]);
  return (
    <div>
      <form className="form" onSubmit={submitHandler}>
        <div>
          <h1>Add Service</h1>
        </div>
        {loading && <LoadingBox></LoadingBox>}
        {error && <MessageBox variant="danger">{error}</MessageBox>}
        <div>
          <label htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            placeholder="Enter Title"
            required
            onChange={(e) => setTitle(e.target.value)}
          ></input>
        </div>
       
        <div>
          <label htmlFor="sort_order">Sort Order</label>
          <input
            type="text"
            id="sort_order"
            placeholder="Enter Order"
            required
            onChange={(e) => setSort_Order(e.target.value)}
          ></input>
        </div>

        <div>
          <label htmlFor="status">Status</label>
          <select name="Status" id="Status" onChange={(e) => setStatus(e.target.value)}>
                        <option value="1">1</option>
                        <option value="0">0</option>
                    </select>
        </div>
       
        <div>
          <label />
          <button className="primary" type="submit">
            Save
          </button>
        </div>
        <div>
          <label />
          <div>
            New user? <Link to={`/register?redirect=${redirect}`}>Create your account</Link>
          </div>
        </div>
      </form>
    </div>
  );
}
