import {
    ADD_SERVICE_FAIL, ADD_SERVICE_REQUEST, ADD_SERVICE_SUCCESS,
    SERVICE_LIST_FAIL, SERVICE_LIST_REQUEST, SERVICE_LIST_SUCCESS
   } from "../constants/serviceConstants";

export const servicelistReducer = (state = { loading: true }, action) => {
    switch (action.type) {
      case SERVICE_LIST_REQUEST:
        return { loading: true };
      case SERVICE_LIST_SUCCESS:
        return { loading: false, listservices: action.payload };
      case SERVICE_LIST_FAIL:
        return { loading: false, error: action.payload };
      default:
        return state;
    }
  };

export const addServiceReducer = (state = {}, action) => {
    switch(action.type)
    {
        case ADD_SERVICE_REQUEST:
            return { loading : true };
        case ADD_SERVICE_SUCCESS:
            return { loading: false, addS: action.payload };
        case ADD_SERVICE_FAIL:
            return { loading: false, error: action.payload };
        default: 
             return state;
    }
}
