import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { detailsAccount } from '../actions/accountActions';
import { addOrder } from '../actions/orderActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import axios from 'axios';
import { ACCOUNT_DETAILS_RESET } from '../constants/accountConstants';

export default function OrderScreen(props) {
  const accountId = props.match.params.id;
  const [name, setName] = useState('');
  const [mobileno, setMobile_No] = useState('');
  const [billing, setBillingAmt] = useState('');
  const [paid, setPaid] = useState('');
  const [product, setProduct] = useState('');
  const [remark, setRemark] = useState('');

  const [pname, setPName] = useState([]);

  useEffect(async () => {
    await axios.get('http://127.0.0.1:8080/api/product/all').then((res) => {
        setPName(res.data);
    })
}, [])

  const redirect = props.location.search
    ? props.location.search.split('=')[1]
    : '/';

    const accountDetails = useSelector((state) => state.accountDetails);
    const { loading, error, account } = accountDetails;


    const postOrder = useSelector((state) => state.postOrder);
  const {
    loading: loadingUpdate,
    error: errorUpdate,
    order: orderAdd,
  } = postOrder;


    const dispatch = useDispatch();
  useEffect(() => {
    if (!account || account._id !== accountId || orderAdd) {
      dispatch({ type: ACCOUNT_DETAILS_RESET });
      dispatch(detailsAccount(accountId));
    } else {
      setName(account.name);
      setMobile_No(account.mobileno);
    }
  }, [props.history, redirect, Update, account, accountId,]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(addOrder(name, mobileno, billing, paid, product, remark));
  };
  
  return (
    <div>
    <form className="form" onSubmit={submitHandler}>
      <div>
        <h1>Update Order</h1>
        <h1>{accountId}</h1>
      </div>
      {loadingUpdate && <LoadingBox></LoadingBox>}
      {errorUpdate && <MessageBox variant="danger">{errorUpdate}</MessageBox>}
      {loading ? (
          <LoadingBox></LoadingBox>
        ) : error ? (
          <MessageBox variant="danger">{error}</MessageBox>
        ) : (
          <>
      <div>
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="Name"
          placeholder="Enter name"
          required
          value={account.Name}
          onChange={(e) => setName(e.target.value)}
        ></input>
      </div>
      
      <div>
        <label htmlFor="mobileno">Mobile No</label>
        <input type="text" id='Mobile_No' placeholder="Enter mobile no" required value={account.Mobile_No} onChange={(e) => setMobile_No(e.target.value)} />      
      </div>

 
      <div>
      <label>Product</label>
      <select name="Name" id="Name" onChange={(e) => setProduct(e.target.value)}>
                                <option>---Select---</option>
                                    {pname && pname.map((obj) => {
                                        return <option value={obj.Name}>{obj.Name}</option>
                                    })
                                    }
                                </select>
      </div>

      <div>
      <label>Billing Amount</label>
      <input
          type="text"
          id="BillingAmt"
          placeholder="Enter amount"
          required
          onChange={(e) => setBillingAmt(e.target.value)}
        ></input>
      </div>


      <div>
      <label>Paid Amount</label>
      <input
          type="text"
          id="PaidAmt"
          placeholder="Enter amount"
          required
          onChange={(e) => setPaidAmt(e.target.value)}
        ></input>
      </div>

      <div>
      <label>Remark</label>
      <input
          type="text"
          id="Remark"
          placeholder="Enter remark"
          required
          onChange={(e) => setRemark(e.target.value)}
        ></input>
      </div>
     
      <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
      </>
        )}
    </form>
  </div>
  );
}
