import { ACCOUNT_GROUP_FAIL, ACCOUNT_GROUP_REQUEST, ACCOUNT_GROUP_SUCCESS } from "../constants/account_groupConstants";

export const account_groupReducer = (state = {}, action) => {
    switch(action.type)
    {
        case ACCOUNT_GROUP_REQUEST:
            return { loading : true };
        case ACCOUNT_GROUP_SUCCESS:
            return { loading: false, userInfo: action.payload };
        case ACCOUNT_GROUP_FAIL:
            return { loading: false, error: action.payload };
        default: 
             return state;
    }
}
