import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router";
import { Link } from "react-router-dom";
import { listProducts } from "../actions/productActions";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import Product from "../components/product";
import Rating from "../components/rating";
import { prices, ratings } from "../utils";

export default function SearchScreen(props) {
    const {
        name = 'all',
        category = 'all',
        min = 0,
        max = 0,
        rating = 0,
        order = 'newest',
        pageNumber = 1,
      } = useParams();
    const dispatch = useDispatch();
    const productList = useSelector((state) => state.productList)
    const { loading, error, products, page, pages } = productList;
    const productCategoryList = useSelector((state) => state.productCategoryList)
    const { loading: loadingCategory, error: errorCategory, categories } = productCategoryList;
    useEffect(() => {
        dispatch(listProducts({ 
        name: name !== 'all' ? name : '',
        category: category !== 'all' ? category : '',
        min,
        max,
        rating,
        order,
        pageNumber
     }));
    }, [category, dispatch, max, min, name, rating, order, pageNumber]);

    const getFilterUrl = (filter) => {
        const filterCategory = filter.category || category;
        const filterName = filter.name || name;
        const filterRating = filter.rating || rating;
        const sortOrder = filter.order || order;
        const filterPage = filter.page || pageNumber;
        const filterMin = filter.min ? filter.min : filter.min === 0 ? 0 : min;
        const filterMax = filter.max ? filter.max : filter.max === 0 ? 0 : max;
        return `/search/category/${filterCategory}/name/${filterName}/min/${filterMin}/max/${filterMax}/rating/${filterRating}/order/${sortOrder}/page/${filterPage}`;
    }
    return (
        <div>
            <div className="row">
            {
                loading ? (<LoadingBox></LoadingBox>)
                :
                error ? (<MessageBox varian="danger">{error}</MessageBox>)
                :
                (
                <div>
                    {products.length} Results
                </div>
                )
            }            
              <div>
              Sort by{' '}
          <select
            value={order}
            onChange={(e) => {
              props.history.push(getFilterUrl({ order: e.target.value }));
            }}
          >
            <option value="newest">Newest Arrivals</option>
            <option value="lowest">Price: Low to High</option>
            <option value="highest">Price: High to Low</option>
            <option value="toprated">Avg. Customer Reviews</option>
          </select>
              </div>
            </div>          
            <div className="row top">
                <div className="col-1">
                    <h3>Department</h3>
                    <div>
                    {
                loadingCategory ? (<LoadingBox></LoadingBox>)
                :
                errorCategory ? (<MessageBox varian="danger">{errorCategory}</MessageBox>)
                :
                (
                    <ul>
                       <li>
                  <Link
                    className={'all' === category ? 'active' : ''}
                    to={getFilterUrl({ category: 'all' })}
                  >
                    Any
                  </Link>
                </li>
                        {categories.map(c => (
                            <li key={c}>
                                <Link className={c === category ? 'active' : ''} to={getFilterUrl({categories})}>{c}</Link>
                            </li>
                        ))}
                    </ul>
                )
            }
                </div>
                <div>
            <h3>Price</h3>
            <ul>
              {prices.map((p) => (
                <li key={p.name}>
                  <Link
                    to={getFilterUrl({ min: p.min, max: p.max })}
                    className={
                      `${p.min}-${p.max}` === `${min}-${max}` ? 'active' : ''
                    }
                  >
                    {p.name}
                  </Link>
                </li>
              ))}
            </ul>
          </div>
          <div>
            <h3>Avg. Customer Review</h3>
            <ul>
              {ratings.map((r) => (
                <li key={r.name}>
                  <Link
                    to={getFilterUrl({ rating: r.rating })}
                    className={`${r.rating}` === `${rating}` ? 'active' : ''}
                  >
                    <Rating caption={' & up'} rating={r.rating}></Rating>
                  </Link>
                </li>
              ))}
            </ul>
          </div> 
                </div>
                <div className="col-3">
                {
                loading ? (<LoadingBox></LoadingBox>)
                :
                error ? (<MessageBox varian="danger">{error}</MessageBox>)
                :
                (
                    <>
                    {products.length === 0 && <MessageBox>No Product Found</MessageBox>}
                    <div className="row center">
                      {products.map((product) => (
                        <Product key={product._id} product={product}></Product>
                      ))}
                    </div>
                    <div>
                      {
                        [...Array(page).keys].map(x => (
                          <Link key={x+1} to={getFilterUrl({ page: x+1 })}>{x+1}</Link>
                        ))
                      }
                    </div>
                  </>
                )
                }
                </div>
            </div>
        </div>
    )
}
