import { ENQUIRY_FAIL, ENQUIRY_REQUEST, ENQUIRY_SUCCESS } from "../constants/enquiryConstants";

export const customerEnquiryReducer = (state = {}, action) => {
    switch(action.type)
    {
        case ENQUIRY_REQUEST:
            return { loading : true };
        case ENQUIRY_SUCCESS:
            return { loading: false, userInfo: action.payload };
        case ENQUIRY_FAIL:
            return { loading: false, error: action.payload };
        default: 
             return state;
    }
}
