import { ORDER_CREATE_REQUEST, ORDER_CREATE_FAIL, ORDER_CREATE_SUCCESS,
    ORDER_LIST_REQUEST, ORDER_LIST_SUCCESS, ORDER_LIST_FAIL, } from "../constants/orderConstants";

export const orderlistReducer = (state = { loading: true }, action) => {
    switch (action.type) {
      case ORDER_LIST_REQUEST:
        return { loading: true };
      case ORDER_LIST_SUCCESS:
        return { loading: false, listOrders: action.payload };
      case ORDER_LIST_FAIL:
        return { loading: false, error: action.payload };
      default:
        return state;
    }
  };

export const addOrderReducer = (state = {}, action) => {
    switch(action.type)
    {
        case ORDER_CREATE_REQUEST:
            return { loading : true };
        case ORDER_CREATE_SUCCESS:
            return { loading: false, orderAdd: action.payload };
        case ORDER_CREATE_FAIL:
            return { loading: false, error: action.payload };
        default: 
             return state;
    }
}
