import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addEnquiry } from '../actions/enquiryActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function OrderScreen(props) {
  const [name, setName] = useState('');
  const [acName, setAcName] = useState([]);
  const [enqId, setEnqID] = useState([]);

  useEffect(async () => {
    await axios.get('http://127.0.0.1:8080/api/account/all').then((res) => {
        setAcName(res.data);
    })
}, [])

  const redirect = props.location.search
    ? props.location.search.split('=')[1]
    : '/';

 
  const addDetails = useSelector((state) => state.addDetails);
  const { enquiryAdd, loading, error } = addDetails;

  const dispatch = useDispatch();

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(addEnquiry(enqId, name));
  };
  useEffect(() => {
    if (enquiryAdd) {
      props.history.push(redirect);
    }
  }, [props.history, redirect, enquiryAdd]);
  
  return (
    <div>
    <form className="form" onSubmit={submitHandler}>
      <div>
        <h1>New Enquiry</h1>
      </div>
      {loading && <LoadingBox></LoadingBox>}
      {error && <MessageBox variant="danger">{error}</MessageBox>}
      <div>
        <label htmlFor="name">Name</label>
        <select name="Name" id="Name" onChange={(e) => setName(e.target.value)}>
                                    {acName.map((obj, index) => {
                                        return <option key={index} value={obj.name}>{obj.Name}</option>
                                    })
                                    }
                                </select>
      </div>
      <div>
        <label htmlFor="EnqID">Enquiry ID</label>
        <input
          type="text"
          id="EnqID"
          value={name}
          onChange={(e) => setEnqID(e.target.value)}
        ></input>
      </div>
  
    <Link to='account'> <h1 style={{textAlign : 'right'}}> Add New</h1> </Link>
      <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
    </form>
  </div>
  );
}
