import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addWork } from '../actions/workActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';

export default function AccountScreen(props) {
  const [worktype, setWorkType] = useState('');
  const [whom, setWhom] = useState('');


  const redirect = props.location.search
    ? props.location.search.split('=')[1]
    : '/';

 
  const workA = useSelector((state) => state.workA);
  const { workadd, loading, error } = workA;

  const dispatch = useDispatch();

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(addWork(worktype, whom));
  };
  useEffect(() => {
    if (workadd) {
      props.history.push(redirect);
    }
  }, [props.history, redirect, workadd]);
  
  return (
    <div>
    <form className="form" onSubmit={submitHandler}>
      <div>
        <h1>Add Work</h1>
      </div>
      {loading && <LoadingBox></LoadingBox>}
      {error && <MessageBox variant="danger">{error}</MessageBox>}
      <div>
        <label htmlFor="worktype">WorkType</label>
        <input
          type="text"
          id="WorkType"
          placeholder="Enter worktype"
          required
          onChange={(e) => setWorkType(e.target.value)}
        ></input>
      </div>
      
      <div>
        <label htmlFor="whom">Person</label>
        <input type="text" id='whom' placeholder="Enter name" required onChange={(e) => setWhom(e.target.value)} />      
      </div>

      <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
    </form>
  </div>
  );
}
