import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addOrder } from '../actions/orderActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import axios from 'axios';

export default function OrderScreen(props) {
  const [name, setName] = useState('');
  const [acName, setAcName] = useState([]);

  useEffect(async () => {
    await axios.get('http://127.0.0.1:8080/api/account/all').then((res) => {
        setAcName(res.data);
    })
}, [])



  const redirect = props.location.search
    ? props.location.search.split('=')[1]
    : '/';

 
  const addDetails = useSelector((state) => state.addDetails);
  const { orderAdd, loading, error } = addDetails;

  const dispatch = useDispatch();

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(addOrder(name));
  };
  useEffect(() => {
    if (orderAdd) {
      props.history.push(redirect);
    }
  }, [props.history, redirect, orderAdd]);
  
  return (
    <div>
    <form className="form" onSubmit={submitHandler}>
      <div>
        <h1>New Order</h1>
      </div>
      {loading && <LoadingBox></LoadingBox>}
      {error && <MessageBox variant="danger">{error}</MessageBox>}
      <div>
        <label htmlFor="name">Name</label>
        <select name="name" id="name" onChange={(e) => setName(e.target.value)}>
                                    {acName && acName.map((obj) => {
                                        return <option value={obj._id}>{obj.Name}</option>
                                    })
                                    }
                                </select>
      </div>

     
      <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
    </form>
  </div>
  );
}
