import { useState, useEffect } from "react";
import axios from 'axios';

const DemoScreen = (props) => {
    const [formData, setFormData] = useState({});
    const [acGroup, setAcGroup] = useState([]);

   
    useEffect(async () => {
        await axios.get('http://127.0.0.1:6000/api/accountGroup/list', config).then((res) => {
            setAcGroup(res.data);
        })
    }, [])


    const submitHandler = async (event) => {
        event.preventDefault();
        if (Validate()) {
          
            const data = new FormData();
            data.append("Name", formData.Name)
            data.append("Mobile_No", formData.Mobile_No)
            data.append("Ac_Group", formData.Ac_Group)
           
            await axios.post('http://127.0.0.1:6000/api/account/', data).then((res) => {
                if (res.status == 200) {
                    setShow(false);
                    alert('Record Added Successfully');
                    setFormData({});
                }
            }).catch((error) => {
                throw error;
            });
        } else {
            alert('Please Fill required data')
        }
    };

    const Validate = () => {
        const fields = ['Name', 'Mobile_No', 'Ac_Group'];
        const data = fields.filter((obj) => formData[obj] == undefined || '')
        if (data && data.length) {
            return false
        } else {
            return true
        }
    }

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setFormData(values => ({ ...values, [name]: value }))
    }
    return (
        <>
           
      <div id="user">
            <form className="form" onSubmit={submitHandler}>
      <div>
        <h1>Add Account </h1>
      </div>
      <div>
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="Name"
          value={formData.Name}
          onChange={handleChange}
        ></input>
      </div>

      <div>
        <label htmlFor="mobileno">Mobile No</label>
        <input
          type="text"
          id="Mobile_No"
          value={formData.Mobile_No}
          onChange={handleChange}
        ></input>
      </div>
     
      
         
               <div style={{width:'30%'}}>
                       
                                <label>Select Account Group</label>
                                <select name="Ac_Group" onChange={handleChange}>
                                <option>---Select---</option>
                                    {acGroup && acGroup.map((obj) => {
                                        return <option value={obj._id}>{obj.ac_group_title}</option>
                                    })
                                    }
                                </select>
                           
                </div>
                <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
    </form>
            </div>
           
        </>
    )
}

export default DemoScreen;
