import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addAccount, grouplist } from '../actions/accountActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import Product from '../components/acgroup';

export default function AccountScreen(props) {
  const [Name, setName] = useState('');
  const [Mobile_No, setMobile_No] = useState('');
  const [Ac_Group, setAc_Group] = useState('');

  const redirect = props.location.search
    ? props.location.search.split('=')[1]
    : '/';

  const addAct = useSelector((state) => state.addAct);
  const { accountAdd, loading, error } = addAct;

  const acgrouplist = useSelector((state) => state.acgrouplist);
  const { listgroups, groupLoading, groupError } = acgrouplist;

  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(grouplist({}))
  })
  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(addAccount(Name, Mobile_No, Ac_Group));
  };
  useEffect(() => {
    if (accountAdd) {
      props.history.push(redirect);
    }
  }, [props.history, redirect, accountAdd]);
  
  return (
    <div>
    <form className="form" onSubmit={submitHandler}>
      <div>
        <h1>Add Account </h1>
      </div>
      {loading && <LoadingBox></LoadingBox>}
      {error && <MessageBox variant="danger">{error}</MessageBox>}
      <div>
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="Name"
          placeholder="Enter name"
          required
          onChange={(e) => setName(e.target.value)}
        ></input>
      </div>

      <div>
        <label htmlFor="mobileno">Mobile No</label>
        <input
          type="text"
          id="Mobile_No"
          placeholder="Enter mobile no"
          required
          onChange={(e) => setMobile_No(e.target.value)}
        ></input>
      </div>
      {loading ? (
        <LoadingBox></LoadingBox>
      ) : error ? (
        <MessageBox variant="danger">{error}</MessageBox>
      ) : (
        <>
          {listgroups.length === 0 && <MessageBox>No Product Found</MessageBox>}
          <div className="row center">
            {listgroups.map((product) => (
              <Product key={product._id} product={product}></Product>
            ))}
          </div>
       
        </>
      )}
     
      <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
    </form>
  </div>
  );
}
