import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addEnquiry } from '../actions/enquiryActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function OrderScreen(props) {
  const [name, setName] = useState('');
  const [acName, setAcName] = useState([]);
  const [eid, setEID] = useState([]);
  const [acid, setAcID] = useState([]);
  const [demo, setDemo] = useState();

  useEffect( () => {
    const getID = async () => {
    const resid = await axios.get('http://127.0.0.1:8080/api/account/all');
    const resacid = await resid.json();
        setAcID(await resacid);
  }
  getID();
  }, []);


const handleID = (e) => {
 const sample = e.target.value;
 setDemo(sample);
}

useEffect( () => {
  const getName = async () => {
  const resname = await axios.get(`http://127.0.0.1:8080/api/account/${demo}`);
  const resn = resname.json();
      setAcName(resn);
}
getName();
}, []);

  const redirect = props.location.search
    ? props.location.search.split('=')[1]
    : '/';

 
  const addDetails = useSelector((state) => state.addDetails);
  const { enquiryAdd, loading, error } = addDetails;

  const dispatch = useDispatch();

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(addEnquiry(name));
  };
  useEffect(() => {
    if (enquiryAdd) {
      props.history.push(redirect);
    }
  }, [props.history, redirect, enquiryAdd]);
  
  return (
    <div>
    <form className="form" onSubmit={submitHandler}>
      <div>
        <h1>New Enquiry</h1>
      </div>
      {loading && <LoadingBox></LoadingBox>}
      {error && <MessageBox variant="danger">{error}</MessageBox>}
      <div>
        <label htmlFor="name">Enquiry</label>
        <select name="EnqID" id="EnqID" onChange={(e) => handleID(e)}>
                                    {acid.map((obj, index) => {
                                        return <option key={index} value={obj.Name}>{obj.AccountID}</option>
                                    })
                                    }
                                </select>
      </div>

      <div>
        <label htmlFor="name">Name</label>
        <select name="Name" id="Name" onChange={(e) => setName(e.target.value)}>
                                    {acName.map((obj, index) => {
                                        return <option key={index} value={obj.Name}>{obj.Name}</option>
                                    })
                                    }
                                </select>
      </div>

    <Link to='account'> <h1 style={{textAlign : 'right'}}> Add New</h1> </Link>
      <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
    </form>
  </div>
  );
}
