import { useState, useEffect } from "react";
import LoadingBox from "../components/LoadingBox";
import MessageBox from "../components/MessageBox";
import { grouplist } from "../actions/accountActions";
import { addAccount } from "../actions/accountActions";
import { useDispatch, useSelector } from 'react-redux';

const DemoScreen = (props) => {
    const [Name, setName] = useState('');
    const [Mobile_No, setMobile_No] = useState('');
   const [Ac_Group, setAc_Group] = useState('');
  
    const acgrouplist = useSelector((state) => state.acgrouplist);
    const { loadingGroup, errorGroup, groups } = acgrouplist;

    const addAct = useSelector((state) => state.addAct);
    const { accountAdd, loading, error } = addAct;

    const dispatch = useDispatch();

    const redirect = props.location.search
    ? props.location.search.split('=')[1]
    : '/';
   
    useEffect(async () => {
      dispatch(grouplist({}));
    }, [dispatch])


    const submitHandler = async (e) => {
      e.preventDefault();
      dispatch(addAccount(Name, Mobile_No, Ac_Group));
    };

    useEffect(() => {
      if (accountAdd) {
        props.history.push(redirect);
      }
    }, [props.history, redirect, accountAdd]);

    return (
        <>
           
      <div id="user">
            <form className="form" onSubmit={submitHandler}>
      <div>
        <h1>Add Account </h1>
      </div>
      {loading && <LoadingBox></LoadingBox>}
      {error && <MessageBox variant="danger">{error}</MessageBox>}
      <div>
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="Name"
          placeholder="Enter name"
          required
          onChange={(e) => setName(e.target.value)}
        ></input>
      </div>

      <div>
        <label htmlFor="mobileno">Mobile No</label>
        <input
          type="text"
          id="Mobile_No"
          placeholder="Enter mobile no"
          required
          onChange={(e) => setMobile_No(e.target.value)}
        ></input>
      </div>
      
         
      
      {loadingGroup ? (
        <LoadingBox></LoadingBox>
      ) : errorGroup ? (
        <MessageBox variant="danger">{errorGroup}</MessageBox>
      ) : (
        <>
       
          <div className="row center">
          <label>Select Account Group</label>
          {groups.length === 0 && <MessageBox>No Product Found</MessageBox>}
                                <select name="Ac_Group" onChange={(e) => setAc_Group(e.target.value)}>
                                <option>---Select---</option>
                                    {groups && groups.map((grouplist) => {
                                        return <option>  <Product key={grouplist._id} product={grouplist}></Product> </option>
                                    })
                                    }
            </select>
          </div>
       
        </>
      )}
                           
                
                <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
    </form>
            </div>
           
        </>
    )
}

export default DemoScreen;
