import { useState, useEffect } from "react";
import { Modal, Button, Row, Form, Table, Container } from "react-bootstrap";
import axios from 'axios';
import { serverUrl } from '../config/config';
import SimpleNavbarComponent from "../components/SimpleNavbar";
import ImageUploader from "react-images-upload";
import SimpleNavbarBottom from '../components/SimpleNavbarBottom';

const DemoScreen = (props) => {
    const [show, setShow] = useState(false);
    const [formData, setFormData] = useState({});
    const [eventSelectOptions, setEventSelectptions] = useState([]);
    const [templates, setTemplates] = useState([]);
    const [templateDelete, setTemplateDelete] = useState({});

   
    useEffect(async () => {
        let config = {
            headers: { Authorization: localStorage.getItem('token') }
        }
        await axios.get(serverUrl + 'templates/getTemplates', config).then((res) => {
            setTemplates(res.data);
        })
    }, [])

    const handleClose = () => setShow(false);

    useEffect(async () => {
        let config = {
            headers: { Authorization: localStorage.getItem('token') }
        }
        await axios.get(serverUrl + 'events/getEvents', config).then((res) => {
            setEventSelectptions(res.data);
        })
    }, [])

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (Validate()) {
            let config = {
                headers: { Authorization: localStorage.getItem('token') }
            }
            const data = new FormData();
            data.append("Event_ID", formData.Event_ID)
            data.append("type", 'Templates')
            data.append("Background", formData.Background)
     //       data.append("Event_Img_ID", formData.Event_Img_ID)
            data.append("Theme_ID", formData.Theme_ID)
            data.append("Theme_ColourID", formData.Theme_ColourID)
            data.append("Subscriber", formData.Subscriber)
   //         data.append("Qty", formData.Qty)

            await axios.post(serverUrl + 'templates/addTemplate', data, config).then((res) => {
                if (res.status == 200) {
                    setShow(false);
                    alert('Record Added Successfully');
                    setFormData({});
                }
            }).catch((error) => {
                throw error;
            });
        } else {
            alert('Please Fill required data')
        }
    };

    const Validate = () => {
        const fields = ['Event_ID', 'Background', 'Theme_ID', 'Theme_ColourID', 'Subscriber'];
        const data = fields.filter((obj) => formData[obj] == undefined || '')
        if (data && data.length) {
            return false
        } else {
            return true
        }
    }

    const handleShow = () => setShow(true);
    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setFormData(values => ({ ...values, [name]: value }))
    }
    const handleFileChange = (e) => {
        const upload_file = e[0];
        setFormData(values => ({ ...values, Background: upload_file }));
    }

    const deleteHandler = (templates) => {
        if (window.confirm('Are you sure to delete?')) {  
            axios.patch(serverUrl + 'templates/deleteTemplates/:id').then((res) => {
                if (res.status === 200) {
                    alert('Record Deleted Successfully');
                    setTemplateDelete({});
                }
            }).catch((error) => {
                throw error;
            });                   
        }
      };

    return (
        <>
           
            <div id="user">
            <form className="form" onSubmit={submitHandler}>
      <div>
        <h1>Add Account </h1>
      </div>
      {loading && <LoadingBox></LoadingBox>}
      {error && <MessageBox variant="danger">{error}</MessageBox>}
      <div>
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="Name"
          placeholder="Enter name"
          required
          onChange={(e) => setName(e.target.value)}
        ></input>
      </div>

      <div>
        <label htmlFor="mobileno">Mobile No</label>
        <input
          type="text"
          id="Mobile_No"
          placeholder="Enter mobile no"
          required
          onChange={(e) => setMobile_No(e.target.value)}
        ></input>
      </div>
     
      
         
               <div style={{width:'30%'}}>
                       
                                <label>Event Id:</label>
                                <select name="Event_ID" onChange={handleChange}>
                                <option>---Select---</option>
                                    {eventSelectOptions && eventSelectOptions.map((obj) => {
                                        return <option value={obj._id}>{obj.Name}</option>
                                    })
                                    }
                                </select>
                           
                </div>
                <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
    </form>
            </div>
           
        </>
    )
}

export default DemoScreen;
