import { WORK_LIST_REQUEST, WORK_LIST_SUCCESS, WORK_LIST_FAIL,
    ADD_WORK_FAIL, ADD_WORK_REQUEST, ADD_WORK_SUCCESS,
    WORK_DETAILS_FAIL,WORK_DETAILS_REQUEST,WORK_DETAILS_SUCCESS } from "../constants/workConstants";

export const worklistReducer = (state = { loading: true }, action) => {
    switch (action.type) {
      case WORK_LIST_REQUEST:
        return { loading: true };
      case WORK_LIST_SUCCESS:
        return { loading: false, listWork: action.payload };
      case WORK_LIST_FAIL:
        return { loading: false, error: action.payload };
      default:
        return state;
    }
  };

export const addWorkReducer = (state = {}, action) => {
    switch(action.type)
    {
        case ADD_WORK_REQUEST:
            return { loading : true };
        case ADD_WORK_SUCCESS:
            return { loading: false, work: action.payload };
        case ADD_WORK_FAIL:
            return { loading: false, error: action.payload };
        default: 
             return state;
    }
}

export const workDetailsReducer = (state = { work:{}, loading: true}, action) =>{
    switch (action.type){
        case WORK_DETAILS_REQUEST:
            return { loading: true };
        case WORK_DETAILS_SUCCESS:
            return { loading: false, work: action.payload };
        case WORK_DETAILS_FAIL:
            return { loading: false, error: action.payload};
        default:
            return state;
    }
};
