import React, { useState, useEffect } from 'react'
import { addService } from '../actions/serviceActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import { useDispatch, useSelector } from 'react-redux';

export default function ServiceScreen(props) {

    const [title, setTitle] = useState('');
    const [sort_order, setSort_Order] = useState('');
    const [status, setStatus] = useState('');

  const redirect = props.location.search
    ? props.location.search.split('=')[1]
    : '/';

  const serviceAdd = useSelector((state) => state.serviceAdd);
  const { addS, loading, error } = serviceAdd;

  const dispatch = useDispatch();
  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(addService(title, sort_order, status));
  };
  useEffect(() => {
    if (addS) {
      props.history.push(redirect);
    }
  }, [props.history, redirect, addS]);
    return (
       <div>
        <form className="form" onSubmit={submitHandler}>
      <div>
        <h1>Add Account</h1>
      </div>
      {loading && <LoadingBox></LoadingBox>}
      {error && <MessageBox variant="danger">{error}</MessageBox>}
      <div>
        <label htmlFor="title">Title</label>
        <input
          type="text"
          id="Title"
          placeholder="Enter Title"
          required
          onChange={(e) => setTitle(e.target.value)}
        ></input>
      </div>
      
      <div>
        <label htmlFor="sort_order">Sort Order</label>
        <input type="text" id='Sort_Order' placeholder="Enter sort order" required onChange={(e) => setSort_Order(e.target.value)} />      
      </div>

 
      <div>
      <label>Select Status</label>
      <select name="Status" id="Status" onChange={(e) => setStatus(e.target.value)}>
                        <option value="1">1</option>
                        <option value="0">0</option>
                    </select>                              
      </div>
     
      <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
    </form>
  </div>
    )
}
