import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addAccount } from '../actions/accountActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import axios from 'axios';

export default function AccountScreen(props) {
  const [Name, setName] = useState('');
  const [Mobile_No, setMobile_No] = useState('');

  const [Ac_Group, setAc_Group] = useState({
    
    ac_group_title: [],
    });
    
    const [ options, setOptions ] = useState(null);
    
    async function getSubjects(){
    const { data } = await axios.get("http://localhost:6000//api/accountGroup/list")
    const option = data.map((item)=>({
        "value" : item._id,
        "label" : item.name
    }))
    setOptions (options)
    }

  const redirect = props.location.search
    ? props.location.search.split('=')[1]
    : '/';

  const addAct = useSelector((state) => state.addAct);
  const { accountAdd, loading, error } = addAct;

  const dispatch = useDispatch();
  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(addAccount(Name, Mobile_No));
  };
  useEffect(() => {
    if (accountAdd) {
      props.history.push(redirect);
    }
  }, [props.history, redirect, accountAdd]);
  
  return (
    <div>
    <form className="form" onSubmit={submitHandler}>
      <div>
        <h1>Add Account </h1>
      </div>
      {loading && <LoadingBox></LoadingBox>}
      {error && <MessageBox variant="danger">{error}</MessageBox>}
      <div>
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="Name"
          placeholder="Enter name"
          required
          onChange={(e) => setName(e.target.value)}
        ></input>
      </div>

      <div>
        <label htmlFor="mobileno">Mobile No</label>
        <input
          type="text"
          id="Mobile_No"
          placeholder="Enter mobile no"
          required
          onChange={(e) => setMobile_No(e.target.value)}
        ></input>
      </div>
      <div>
      <label htmlFor="Ac_Group">Account Group</label>
      <select
  options={options}
  name="ac_group_title"
  isMulti
  value={Ac_Group.ac_group_title}
  onChange={(selectedValues) => {
    setAc_Group((preValue) => {
      return {
        ...preValue,
        subjects: selectedValues,
      };
    });
  }}
></select>
</div>
      <div>
        <label />
        <button className="primary" type="submit">
          Save
        </button>
      </div>
    </form>
  </div>
  );
}
